INSERT INTO `news` (`id`, `title`, `file`, `image`, `file_attach`, `news_type`, `master_id`, `user_id_insert`, `user_id_update`, `date_time`, `comment`, `related_id`, `related_type_name`) VALUES
(98966, 'Почти завершен процесс создания дизайна портала', 'Надеемся, что созданный дизайн Вам прийдется по вкусу. В дальнейшем возможна разработка нескольких тем дизайна, которые Вы сможете выбрать индивидуально под себя. \r\nТак например если в базовом варианте дизайн носит умеренно строгий характер, то многим, возможно, захочется добавить большей красочности. Таким обрзом планируется реализацию скиновой структуры дизайна сайта, при котором после того, как Вы попали на портал - Вы можете выбрать одну из нескольких схем оформления портала, эта схема будет сохранена у Вас локально и в следующий раз не потребует своего выбора.\r\nПланируется разработка трех тем оформления: консервативный, умеренно консервативный, модерн.\r\nПричем это не затронет базовый дизайн сайта для остальных пользователей портала', 'news98966.jpg', '', '', 0, 0, 28, '2006-05-22 16:45:04', '', 0, ''),
(820574, 'Пересдача зачета по CASE', 'Вниманию студентов 2 курса специальности ПИЭ!\r\nПересдача зачета по системному моделированию и CASE-технологиям состоится 17 июня с 13.30 до 15.00. Ауд. 6-315', '', '', 'notice', 0, 42, 0, '2009-06-16 15:03:24', '', 0, ''),
(820575, 'Пересдача курсовых проектов', 'Вниманию студентов группы ПИЭ-121!\r\nПоследняя пересдача курсовых работ по дисциплине "Информатика и программирование" состоится 18 июня, в четверг, с 13.30 до 15.00. ауд. 6-315.', '', '', 'notice', 0, 42, 0, '2009-06-16 15:09:04', '', 0, ''),
(820577, 'Собрание по проведению практики для ПИЭ-4', 'Вниманию студентов групп ПИЭ-413, 414, 414д!\r\nОрганизационное собрание по производственной практике состоится 24.06.09 в 15.00 в ауд. 6-205.\r\nЯвка обязательна!', '', '', 'notice', 0, 37, 0, '2009-06-19 12:07:06', '', 0, ''),
(115030, 'Нормоконтроль дипломного проекта', 'После прохождения предзащиты необходимо представить дипломный проект для нормоконтроля. Консультант - Бакусова Наталья Сергеевна. С 16.00 в Вт и ЧТ', 'news115030.jpg', '', '', 0, 0, 28, '2006-05-23 19:38:15', '', 0, ''),
(99683, 'Безопасность и экологичность проекта', 'Напоминаем, что консультантов по этим разделам можно найти в ПН и ЧТ с 16.00 до 17.00.\r\nАудитория по экономической части проекта- 3-205,\r\n                     по безопасности и эклогичности- 4-305', 'news99683.jpg', '', '', 0, 0, 28, '2006-05-31 14:40:02', '', 0, ''),
(567454, 'Проходят последние предзащиты', 'Выполнение дипломных проектов близится к своему логическому завершению, хотя некоторым еще требуется пройти множество согласований, вцелом наблюдается почти полная готовность к защите.', 'news567454.jpg', '', '', 0, 0, 28, '2006-06-03 09:59:36', '', 0, ''),
(816403, 'По работе портала', 'Будем рады любой помощи в наполнении портала учебной информацией. О всех замеченых недочетах в работе портала и Ваших предложениях просим писать на указанные адреса разработчиков.', '', '', '', 0, 0, 28, '2006-06-12 10:58:00', '', 0, ''),
(101325, 'Впишите свое имя в портал преподавателей', 'Если Вы хотите принять участие в сборе новостей для портала в раздел "Новости" - пишите. Лучшие наркоры будут отмечены на страницах портала.', '', '', '', 0, 0, 28, '2006-06-24 15:08:32', '', 0, ''),
(820265, 'Завершены защиты дипломных проектов специальности АСОИУ', 'Поздравления всем студентам, а теперь уже инженерам - системотехникам.', '', '', '', 0, 0, 28, '2006-06-18 21:07:54', '', 0, ''),
(154321, 'Обсуждение проекта внедрения портала', 'Первоначально самая последняя версия портала будет доступна только внутри локальной сети УГАТУ.', 'news154321.JPG', '', '', 0, 0, 28, '2006-10-12 23:40:52', '', 0, ''),
(820266, 'Зачет по статистике', 'Требования к зачету по статистике: \r\n1) греческий алфавит (большие и маленькие буквы и их названия) \r\n2) умение решать задачи из методички по практике \r\n[b]Допуск к зачету[/b] - защита курсовой работы.', '', '', 'notice', 0, 27, 28, '2006-12-14 18:25:28', '', 0, ''),
(28, 'C Новым 2007 Годом', 'Поздравляю всех сотрудников кафедры и студентов, аспирантов и магистрантов, и всех тех, для кого образование играет не последнюю роль.\r\nСчастья, удачи, творческих успехов и всего самого лучшего, что можно пожелать в Новом 2007 Году.Пусть все Ваши мечты сбудутся !\r\n\r\nАдминистрация портала кафедры АСУ УГАТУ.', 'news28_12_2006_1.txt.jpg', '', '', 0, 0, 28, '2006-12-28 17:51:49', '', 0, ''),
(820462, 'УВАЖАЕМЫЕ СТУДЕНТЫ ФИРТ!', 'Только для Вас ! Предлагается эксклюзивная помощь коуча!\r\n\r\nКоучинг – это индивидуальная работа, в которой Вы получите немало выгод и моя задача помочь Вам:\r\n\r\n•	адаптироваться в группе\r\n•	подготовиться к защите дипломной работы\r\n•	организовать свою научную работу\r\n•	эффективно взаимодействовать с преподавателями\r\n•	эффективно взаимодействовать с научным           руководителем\r\n•	найти Ваши ресурсы и таланты\r\n•	привести к разумному балансу свою нынешнюю жизнь\r\n•	улучшить качество взаимоотношений\r\n•	справляться с приступами плохого настроения\r\n•	обрести вкус к жизни и т.д.\r\n\r\n\r\nЯ буду рада помочь Вам!\r\n\r\nМне можно позвонить  8-917-41-54-757\r\nИли написать  coach-newtime@list.ru\r\n\r\nКоуч-практик Герасимова Ильмира Барыевна', '', '', '', 0, 43, 43, '2008-10-22 18:48:29', '', 0, ''),
(15, 'Студентам 5-го курса', 'Срочно принести в ауд. [u]6-322[/u] ксерокопии паспортных данных и список тем курсовых работ', '', '', '', 0, 0, 28, '2007-01-15 16:45:05', '', 0, ''),
(2, 'Вопросы для ПИЭ по междисциплинарному экзамену', 'Вопросы по междисциплинарному экзамену дополнительно к ПИС размещены на данном портале в разделе ДОКУМЕНТЫ', '', '', '', 0, 0, 28, '2007-03-02 14:26:39', '', 0, ''),
(6, 'C 8 Марта', 'Самых светлых пожеланий в канун праздника.\r\n\r\nДля Вас праздничная открытка.\r\n\r\nАдминистрация портала', 'news06_03_2007_1.txt.jpg', '1859434.swf', '', 0, 0, 28, '2007-03-06 17:59:48', '', 0, ''),
(820274, 'Лекции по дисциплине', 'Вниманию студентов 1 курса специальности ПИЭ! \r\nЛекции по программированию находятся на сайте в разделе Учеба (найдите дисциплину "Информатика и программирование"). \r\nПожалуйста, сделате копии и принесите распечатки лекций на первую лекцию, которая состоится на [b]3 неделе[/b].', '', '', 'notice', 0, 26, 28, '2007-02-09 19:58:03', '', 0, ''),
(12, 'Вниманию кураторов', '[b]В среду [b]14 февраля 2007[/b] года в 11:30 в ауд. 6-415 состоится кураторский час для кураторов 1 курса.\r\nЯвка строго обязательна!', '', '', '', 0, 0, 28, '2007-02-12 16:35:58', '', 0, ''),
(820273, 'ПИЭ 3 курс и ПИЭ 5 курс вечерники', 'Задолженности по лабораторным работам и экзамен принимаю в понедельник (5 февраля 2007 года)', '', '', 'notice', 0, 38, 28, '2007-02-03 15:29:19', '', 0, ''),
(820347, 'Вопросы к экзамену', 'На сайте в разделе [b]Учеба[/b] выложены вопросы к экзамену по предмету  "Организация и планирование производства"', '', '', 'notice', 0, 51, 0, '2007-11-23 16:56:06', '', 0, ''),
(820412, '"Неделя науки": НАГРАЖДЕНИЕ ПОБЕДИТЕЛЕЙ', 'ПОБЕДИТЕЛИ  "Недели науки" по секции АСУ - Ишбулатова Д.Р. (АСОИ-526), Шамсиева А.М. (ПИЭ-508) - ВНИМАНИЕ!!!\r\n\r\nВместе с Вашими руководителями (доц. Федорова Н.И., проф. Арьков В.Ю.) Вы приглашаетесь на НАГРАЖДЕНИЕ, которое \r\nсостоится 28 АПРЕЛЯ, 14-00, 3-Й АКТОВЫЙ ЗАЛ. \r\nПросьба руководителям обеспечить явку!', '', '', 'notice', 0, 47, 0, '2008-04-21 13:04:16', '', 0, ''),
(820380, 'Студентам ПИЭ 1 курса!', 'Выдача заданий на курсовое проектирование для студентов групп ПИЭ-118, ПИЭ-119 состоится в четверг, 20.03.08 в 12.10, ауд. 6-213. Явка строго обязательна!', '', '', 'notice', 0, 42, 0, '2008-03-11 15:36:12', '', 0, ''),
(820381, 'Прием задолженностей', 'Прием задолженностей  состоится в четверг  13 марта с 11.00 в ауд. 6-315. Для сдачи экзамена при себе иметь индивидуальные ведомости.\r\n', '', '', 'notice', 0, 51, 51, '2008-03-11 17:25:55', '', 0, ''),
