(60, 'Распоряжение - Специальность', 'seb.speciality', '', 4, '$value = "";\r\n            $commission = $object->commission;\r\n            if (!is_null($commission)) {\r\n                foreach ($commission->diploms->getItems() as $diplom) {\r\n                    $student = $diplom->student;\r\n                    if (!is_null($student)) {\r\n                        $group = $student->group;\r\n                        if (!is_null($group)) {\r\n                            $corriculum = $group->corriculum;\r\n                            if (!is_null($corriculum)) {\r\n                                $direction = $corriculum->direction;\r\n                                if (!is_null($direction)) {\r\n                                    if ($direction->comment <> "") {\r\n                                        $value = $direction->comment;\r\n                                    } elseif ($direction->getValue() <> "") {\r\n                                        $value = $direction->getValue();\r\n                                    }\r\n                                }\r\n                            }\r\n                        }\r\n                    }\r\n                }\r\n            }', 0, 1, ''),
