(62, 'Распоряжение - Студенты', 'seb.students', '', 4, '            $value = array();\r\n            $commission = $object->commission;\r\n            if (!is_null($commission)) {\r\n            	$students = array();\r\n            	$protocols = array();\r\n            	foreach ($commission->diploms->getItems() as $diplom) {\r\n            		if (strtotime($diplom->date_act) == strtotime($object->date_act)) {\r\n            			$protocol = $diplom->protocol;\r\n            			$student = $diplom->student;\r\n            			if (!is_null($student)) {\r\n            				$students[] = $student->getName();\r\n            			}\r\n            			if (!is_null($protocol)) {\r\n            				$protocols[] = $protocol;\r\n            			}\r\n            		}\r\n            	}\r\n            	array_multisort($protocols, SORT_ASC, $students, SORT_ASC);\r\n            	$counter = 0;\r\n            	foreach ($students as $student) {\r\n            		$counter++;\r\n            		$value[] = array(\r\n            				$counter,\r\n            				$student\r\n            		);\r\n            	}\r\n            }', 0, 2, ''),
(63, 'Распоряжение - Студенты для бюллетений', 'seb.students2', '', 4, '$value = new CArrayList();\r\n            $commission = $object->commission;\r\n            if (!is_null($commission)) {\r\n                $students = array();\r\n                foreach ($commission->diploms->getItems() as $diplom) {\r\n                    if (strtotime($diplom->date_act) == strtotime($object->date_act)) {\r\n                        $student = $diplom->student;\r\n                        if (!is_null($student)) {\r\n                            $students[$student->getName()] = $student->getId();\r\n                        }\r\n                    }\r\n                }\r\n                ksort($students);\r\n                foreach ($students as $id) {\r\n                    $student = CStaffManager::getStudent($id);\r\n                    if (!is_null($student)) {\r\n                        $value->add($student->getId(), $student);\r\n                    }\r\n                }\r\n            }', 0, 1, 'table-row'),
