(67, 'Постановление - Студенты', 'resolution.students', '', 4, '            $value = array();\r\n            $commission = $object->commission;\r\n            if (!is_null($commission)) {\r\n            	$students = array();\r\n            	$protocols = array();\r\n            	foreach ($commission->diploms->getItems() as $diplom) {\r\n            		$row = array(\r\n            				"тов.",\r\n            				"",\r\n            				"на",\r\n            				""\r\n            		);\r\n            		if (strtotime($diplom->date_act) == strtotime($object->date_act)) {\r\n            			$student = $diplom->student;\r\n            			$protocol = $diplom->protocol;\r\n            			if (!is_null($student)) {\r\n            				$row[1] = $student->getName();\r\n            			}\r\n            			$mark = $diplom->mark;\r\n            			if (!is_null($mark)) {\r\n            				$row[3] = $mark->getValue();\r\n            			}\r\n            			if (!is_null($student)) {\r\n            				$students[$student->getName()] = $row;\r\n            			}\r\n            			if (!is_null($protocol)) {\r\n            				$protocols[] = $protocol;\r\n            			}\r\n            		}\r\n            	}\r\n            	array_multisort($protocols, SORT_ASC, $students, SORT_ASC);\r\n            	foreach ($students as $row) {\r\n            		$value[] = $row;\r\n            	}\r\n            }', 0, 2, ''),
(68, 'Постановление - Квалификация', 'resolution.qualification', '', 4, '$value = "";\r\n            $commission = $object->commission;\r\n            if (!is_null($commission)) {\r\n                foreach ($commission->diploms->getItems() as $diplom) {\r\n                    $student = $diplom->student;\r\n                    if (!is_null($student)) {\r\n                        $group = $student->group;\r\n                        if (!is_null($group)) {\r\n                            $corriculum = $group->corriculum;\r\n                            if (!is_null($corriculum)) {\r\n                                $qualification = $corriculum->qualification;\r\n                                if (!is_null($qualification)) {\r\n                                    $value = $qualification->getValue();\r\n                                    break;\r\n                                }\r\n                            }\r\n                        }\r\n                    }\r\n                }\r\n            }', 0, 1, ''),
(69, 'Постановление - Специальность', 'resolution.speciality', '', 4, '$value = "";\r\n            $commission = $object->commission;\r\n            if (!is_null($commission)) {\r\n                foreach ($commission->diploms->getItems() as $diplom) {\r\n                    $student = $diplom->student;\r\n                    if (!is_null($student)) {\r\n                        $group = $student->group;\r\n                        if (!is_null($group)) {\r\n                            $corriculum = $group->corriculum;\r\n                            if (!is_null($corriculum)) {\r\n                                $direction = $corriculum->direction;\r\n                                if (!is_null($direction)) {\r\n                                    if ($direction->comment !== "") {\r\n                                        $value = $direction->comment;\r\n                                        break;\r\n                                    } else {\r\n                                        $value = $direction->getValue();\r\n                                        break; \r\n                                    }\r\n                                }\r\n                            }\r\n                        }\r\n                    }\r\n                }\r\n            }', 0, 1, ''),
