(72, 'Постановление - Рекомендован в аспирантуру', 'resolution.recomendation', '', 4, '$value = "";\r\n            $commission = $object->commission;\r\n            if (!is_null($commission)) {\r\n                $students = array();\r\n                foreach ($commission->diploms->getItems() as $diplom) {\r\n                    if (strtotime($diplom->date_act) == strtotime($object->date_act)) {\r\n                        if ($diplom->aspire_recomendation == "1") {\r\n                            $student = $diplom->student;\r\n                            if (!is_null($student)) {\r\n                                $students[] = $student->getName();\r\n                            }\r\n                        }\r\n                    }\r\n                }\r\n                sort($students);\r\n                $value = implode(", ", $students);\r\n            }', 0, 1, ''),
(73, 'Постановление - С отличием', 'resolution.perfects', '', 4, '$value = "";\r\n            $commission = $object->commission;\r\n            if (!is_null($commission)) {\r\n                $students = array();\r\n                foreach ($commission->diploms->getItems() as $diplom) {\r\n                    if (strtotime($diplom->date_act) == strtotime($object->date_act)) {\r\n                        if ($diplom->isPerfect()) {\r\n                            $student = $diplom->student;\r\n                            if (!is_null($student)) {\r\n                                $students[] = $student->getName();\r\n                            }\r\n                        }\r\n                    }\r\n                }\r\n                sort($students);\r\n                $value = implode(", ", $students);\r\n            }', 0, 1, ''),
