(81, 'Протокол - Члены комиссии', 'protocol.members', '', 4, '$value = array();\r\n            $commission = $object->commission;\r\n            if (!is_null($commission)) {\r\n                $members = array();\r\n                foreach ($commission->members->getItems() as $member) {\r\n                    $members[] = $member->getName();\r\n                }\r\n                if (count($members) % 2 == 1) {\r\n                    $members[] = "";\r\n                }\r\n                $half = count($members) / 2;\r\n                for ($i = 0; $i < $half; $i++) {\r\n                    $value[] = array(\r\n                        ($i + 1),\r\n                        $members[$i],\r\n                        ($i + $half + 1),\r\n                        $members[$i + $half]\r\n                    );\r\n                }\r\n            }', 0, 2, ''),
