(89, 'Протокол - Пол студента', 'protocol.gender', '', 4, '$value = "студенту";\r\n            $student = $object->student;\r\n            if (!is_null($student)) {\r\n                $gender = $student->gender;\r\n                if (!is_null($gender)) {\r\n                    if ($gender->getValue() == "мужской") {\r\n                        $value = "студенту";\r\n                    } else {\r\n                        $value = "студентке";\r\n                    }\r\n                }\r\n            }', 0, 1, ''),
