(91, 'Протокол - Члены в конце', 'protocol.members2', '', 4, '$value = array();\r\n            $commission = $object->commission;\r\n            if (!is_null($commission)) {\r\n                $isFirst = true;\r\n                foreach ($commission->members->getItems() as $person) {\r\n                    if ($isFirst) {\r\n                        $value[] = array(\r\n                            "Члены экзаменационной комиссии",\r\n                            "",\r\n                            $person->getName()\r\n                        );\r\n                        $isFirst = false;\r\n                    } else {\r\n                        $value[] = array(\r\n                            "",\r\n                            "",\r\n                            $person->getName()\r\n                        );\r\n                    }\r\n                }\r\n            }', 0, 2, ''),
