(94, 'Нагрузка аудиторная', 'load_classroom', '', 1, '$value = "";\r\n            $group = $object->getGroup();\r\n            if (!is_null($group)) {\r\n                $corriculum = $group->corriculum;\r\n                if (!is_null($corriculum)) {\r\n                    $value = $corriculum->load_classroom;\r\n                }\r\n            }', 0, 1, ''),
(95, 'Оценка за производственно-технологическую практику', 'student_practice_pr-tech_mark', '', 1, '$discipline = 394;\r\n\r\n\r\n            $value = "";\r\n            /**\r\n             * Получаем оценку студента по выбранной дисциплине.\r\n             * Пока получаем просто последнюю оценку вне зависимости\r\n             * от формы контроля по дисциплине\r\n             */\r\n            $query = new CQuery();\r\n            $query->select("act.id, mark.name as name")\r\n                ->from(TABLE_STUDENTS_ACTIVITY." as act")\r\n                ->condition("act.student_id = ".$object->getId()." and act.kadri_id = 380 and act.subject_id = ".$discipline." and act.study_act_id in (1, 2, 12, 14)")\r\n                ->leftJoin(TABLE_MARKS." as mark", "mark.id = act.study_mark")\r\n                ->order("act.id asc");\r\n            foreach ($query->execute()->getItems() as $item) {\r\n                $value = $item["name"];\r\n            }    ', 0, 1, ''),
(96, ' 	Длительность производственно-технологической практики (определяется по учебному плану) ', 'student_practice_pr-tech_length', '', 1, 'if (is_null($object->getGroup()->corriculum)) {\r\n                $value = "__________";\r\n            } elseif (is_null($object->getGroup()->corriculum->getPracticeByAbbreviatedName("ПТП"))) {\r\n                $value = "__________";\r\n            } else {\r\n                $p = $object->getGroup()->corriculum->getPracticeByAbbreviatedName("ПТП");\r\n                $number = $p->length;\r\n                $value = $number." ".CUtils::getNumberInCase($number, "неделя", "недели", "недель");\r\n            }', 0, 0, ''),
(97, 'Протокол - Вопросы', 'protocol.questions', '', 4, '$value = "";\r\n            /**\r\n             * Параметры в формате\r\n             * id специальности => id набора билетов\r\n             */\r\n            $params = array(\r\n                1 => 1371200960, 2 => 1371125759, 9 => 1371125759, 10 => 1371125759, 79 => 1371810387\r\n            );\r\n            $student = $object->student;\r\n            if (!is_null($student)) {\r\n                $corriculum = $student->getCorriculum();\r\n                if (!is_null($corriculum)) {\r\n                    $direction = $corriculum->direction;\r\n                    if (!is_null($direction)) {\r\n                        if (array_key_exists($direction->getId(), $params)) {\r\n                            $questionSet = $params[$direction->getId()];\r\n                            $questions = CExamManager::getTicketsBySession($questionSet);\r\n                            if ($questions->getCount() > 0) {\r\n                                $text = array();\r\n                                $ticket = $questions->getShuffled()->getFirstItem();\r\n                                foreach ($ticket->questions->getItems() as $q) {\r\n                                    $text[] = $q->text;\r\n                                }\r\n                                $value = implode(" ", $text);\r\n                            }\r\n                        }\r\n                    }\r\n                }\r\n            }', 0, 1, ''),
(98, 'Протокол - СтудентФИОРодительный падеж', 'protocol.student_rp', '', 4, '$value = "";\r\n            $student = $object->student;\r\n            if (!is_null($student)) {\r\n                if ($student->fio_rp == "") {$value = $student->getName();} else {$value = $student->fio_rp;}\r\n            }', 0, 1, '');
INSERT INTO `print_field` (`id`, `title`, `alias`, `description`, `formset_id`, `value_evaluate`, `parent_id`, `type_id`, `parent_node`) VALUES
(100, 'Ведомость на оплату консультантов СТУДЕНТОВ', 'commission_sheet_consult_students', '', 3, '$value = array();\r\n            /**\r\n             * Получаем все дипломы, которые защищаются в выбранной\r\n             * комиссии\r\n             */\r\n            $diploms = new CArrayList();\r\n            foreach ($object->diploms->getItems() as $diplom) {\r\n                $diploms->add($diplom->getId(), $diplom);\r\n            }\r\n            /**\r\n             * Теперь собираем всех рецензентов в один массив.\r\n             * К каждому рецензенту прицепляем дипломы, которые он\r\n             * рецензировал\r\n             */\r\n            $reviewers = new CArrayList();\r\n            foreach ($diploms->getItems() as $diplom) {\r\n                /**\r\n                 * Консультант\r\n                 */\r\n                if (!is_null($diplom->person)) {\r\n                    $reviewer = $diplom->person;\r\n                    $reviewerArr = new CArrayList();\r\n                    if ($reviewers->hasElement($reviewer->getId())) {\r\n                        $reviewerArr = $reviewers->getItem($reviewer->getId());\r\n                    }\r\n                    $reviewerArr->add($diplom->getId(), $diplom);\r\n                    $reviewers->add($reviewer->getId(), $reviewerArr);\r\n                }\r\n            }\r\n            /**\r\n             * Теперь выводим это в окончательный массив\r\n             */\r\n            $reviewerIndex = 0;\r\n            foreach ($reviewers->getItems() as $reviewerId=>$diploms) {\r\n                $reviewerIndex++;\r\n                $isFirst = true;\r\n                foreach ($diploms->getItems() as $diplom) {\r\n                    $dataRow = array();\r\n                    /**\r\n                     * Для начала заполним результирующий массив пустыми строками\r\n                     */\r\n                    for ($i = 0; $i <= 7; $i++) {\r\n                        $dataRow[$i] = "";\r\n                    }\r\n                    /**\r\n                     * Если это первая строка, то выводим инфу по рецензенту\r\n                     * и диплому. В противном случае, только по диплому\r\n                     */\r\n                    if ($isFirst) {\r\n                        $isFirst = false;\r\n                        /**\r\n                         * Вся информация по рецензенту или руководителю\r\n                         *\r\n                         * Номер рецензента\r\n                         */\r\n                        $dataRow[0] = "";\r\n                        /**\r\n                         * ФИО, ученая степень, звание\r\n                         */\r\n                        $dataRow[1] = "";\r\n                        $reviewer = CStaffManager::getPerson($reviewerId);\r\n                        if (!is_null($reviewer)) {\r\n                            $nv = "";\r\n                            $nv = $reviewer->getName();\r\n                            /**\r\n                             * Степень\r\n                             */\r\n                            if (!is_null($reviewer->degree)) {\r\n                                $nv .= ", ".$reviewer->degree->getValue();\r\n                            }\r\n                            /**\r\n                             * Звание\r\n                             */\r\n                            if (!is_null($reviewer->title)) {\r\n                                $nv .= ", ".$reviewer->title->getValue();\r\n                            }\r\n                            $dataRow[1] = $nv;\r\n                            /**\r\n                             * Дата рождения\r\n                             */\r\n                            $dataRow[2] = "";\r\n                            if ($reviewer->date_rogd != "") {\r\n                                $dataRow[2] = date("d.m.Y", strtotime($reviewer->date_rogd));\r\n                            }\r\n                            /**\r\n                             * Паспортные данные\r\n                             * номер, серия, кем и когда выдан\r\n                             * ИНН, СНИЛС\r\n                             */\r\n                            $nv = "";\r\n                            if ($reviewer->passp_seria != "") {\r\n                                $nv = $reviewer->passp_seria;\r\n                            }\r\n                            if ($reviewer->passp_nomer != "") {\r\n                                if ($nv == "") {\r\n                                    $nv = $reviewer->passp_nomer;\r\n                                } else {\r\n                                    $nv .= " ".$reviewer->passp_nomer;\r\n                                }\r\n                            }\r\n                            if ($reviewer->passp_place != "") {\r\n                                if ($nv == "") {\r\n                                    $nv = "выдан ".$reviewer->passp_place;\r\n                                } else {\r\n                                    $nv .= " выдан ".$reviewer->passp_place;\r\n                                }\r\n                            }\r\n                            if ($reviewer->INN != "") {\r\n                                if ($nv == "") {\r\n                                    $nv = $reviewer->INN;\r\n                                } else {\r\n                                    $nv .= ", ".$reviewer->INN;\r\n                                }\r\n                            }\r\n                            if ($reviewer->insurance_num != "") {\r\n                                if ($nv == "") {\r\n                                    $nv = $reviewer->insurance_num;\r\n                                } else {\r\n                                    $nv .= ", ".$reviewer->insurance_num;\r\n                                }\r\n                            }\r\n                            $dataRow[3] = $nv;\r\n                            /**\r\n                             * Полный домашний адрес\r\n                             */\r\n                            $dataRow[4] = $reviewer->add_home;\r\n                            /**\r\n                             * Номер и дата приказа\r\n                             */\r\n                            $dataRow[5] = "";\r\n                            if (!is_null($object->year)) {\r\n                                $order = $reviewer->getSABOrderByYearAndType($object->year, "order_consult");\r\n                                if (!is_null($order)) {\r\n                                    if (!is_null($order->order)) {\r\n                                        $dataRow[5] = $order->order->getName();\r\n                                    }\r\n                                }\r\n                            }\r\n                            /**\r\n                             * Количество часов. По числу дипломников * ставку\r\n                             */\r\n                            $rate = 0;\r\n                            if (!is_null($object->year)) {\r\n                                $rateObj = CRatesManager::getRateByAliasAndYear($object->year, "sab_consult_student");\r\n                                if (!is_null($rateObj)) {\r\n                                    $rate = $rateObj->value;\r\n                                }\r\n                            }\r\n                            $dataRow[7] = $rate * $diploms->getCount();\r\n                        }\r\n                    }\r\n                    /**\r\n                     * Фамилия и инициалы дипломника\r\n                     */\r\n                    $dataRow[6] = "";\r\n                    if (!is_null($diplom->student)) {\r\n                        $student = $diplom->student;\r\n                        $nv = "";\r\n                        /**\r\n                         * ФИО\r\n                         */\r\n                        $nv = $student->getName();\r\n                        /**\r\n                 