        		/**\r\n	            		 * Выходные данные\r\n	            		 */\r\n            		if (!is_null($object->bibliografya)) {\r\n            			/**\r\n            			 * Полная библиография\r\n            			 */\r\n            			$nv = "";\r\n            			$nv = $object->bibliografya;\r\n            			/**\r\n            			 * Издательство\r\n            			 */\r\n            			if (!is_null($object->publisher)) {\r\n            				$nv .= ". ".$object->publisher;\r\n            			}\r\n            			/**\r\n            			 * Год\r\n            			 */\r\n            			if (!is_null($object->year)) {\r\n            				$nv .= ", ".$object->year;\r\n            			}\r\n            			/**\r\n            			 * Страницы\r\n            			 */\r\n            			if (!is_null($object->page_range)) {\r\n            				$nv .= ", - с.".$object->page_range;\r\n            			}\r\n            			$dataRow[3] = $nv;\r\n            		}\r\n            		if (!is_null($object->volume)) {\r\n            			/**\r\n            			 * Общий объем в стр.\r\n            			 */\r\n            			$nv = "";\r\n            			$nv = $object->volume;\r\n            			/**\r\n            			 * Стр. соискателя\r\n            			 */\r\n            			if ($object->authors->getCount() > 0) {\r\n            				$soavt = $object->authors->getCount();\r\n            			}\r\n            			if (isset($soavt)) {\r\n            			$str = $nv/$soavt;\r\n            			$nv .= "/".round($str);\r\n            			}\r\n            			$dataRow[4] = $nv;\r\n            		}\r\n                        /**\r\n            		 * Авторы с кафедры\r\n            		 */\r\n            		$avtors = array();\r\n            		$avts = array();\r\n            		if (!is_null($object->authors)) {\r\n            			foreach ($object->authors->getItems() as $author) {\r\n            				$avtors[] = $author->id;\r\n            			}\r\n            		}\r\n            		/**\r\n            		 * Убираем из авторов с кафедры выбранного сотрудника\r\n            		 */\r\n            		parse_str(parse_url($_SERVER["HTTP_REFERER"], PHP_URL_QUERY));\r\n            		if (isset($avtors)) {\r\n	            		if (isset($person) and $person != 0) {\r\n	            			$avtDel = $person;\r\n	            		} elseif (!isset($person)) {\r\n	            			$avtDel = CSession::getCurrentPerson()->id;\r\n	            		} elseif (isset($person) and $person == 0) {\r\n	            			$avtDel = CSession::getCurrentPerson()->id;\r\n	            		}\r\n	            		while (($i = array_search($avtDel, $avtors)) !== false) {\r\n	            			unset($avtors[$i]);\r\n	            		}\r\n	            		foreach ($avtors as $avtor) {\r\n	            			$auth = CStaffManager::getPersonById($avtor);\r\n	            			$avts[] = $auth->fio_short;\r\n	            		}\r\n            		}\r\n            		/**\r\n            		 * Соавторы\r\n            		 */\r\n            		if (isset($avts) and sizeof($avtors) != 0) {\r\n            			$authors = implode(", ", $avts);\r\n            			if ($object->authors_all != "") {\r\n            				$authors .= ", ".$object->authors_all;\r\n            			} \r\n            		} elseif (!isset($avts)) {\r\n            			$authors = $object->authors_all;\r\n            		} else {\r\n            			$authors = $object->authors_all;\r\n            		}\r\n            		$dataRow[5] = $authors;\r\n            		$value[] = $dataRow;\r\n            	}\r\n             }', 0, 2, ''),
